import React, { useState, useEffect } from "react";
import { getAutores, getAutorPorId } from "..";
import { Card, CardBody, CardHeader, CardTitle, Table, Row, Col } from "reactstrap";
import PanelHeader from "components/PanelHeader/PanelHeader.js";
import { thead } from "variables/general";

function RegularTables() {
  const [autores, setAutores] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await getAutores();
        setAutores(data);
      } catch (error) {
        // Manejar el error
      }
    };

    fetchData();
  }, []);

  const obtenerAutorPorId = async (id) => {
    try {
      const autor = await getAutorPorId(id);
      console.log("Autor por ID:", autor);
    } catch (error) {
      // Manejar el error
    }
  };

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col xs={12}>
            <Card>
              <CardHeader>
                <CardTitle tag="h4">Autores</CardTitle>
              </CardHeader>
              <CardBody>
                <Table responsive>
                  <thead className="text-primary">
                    <tr>
                      {thead.map((prop, key) => {
                        if (key === thead.length - 1)
                          return (
                            <th key={key} className="text-right">
                              {prop}
                            </th>
                          );
                        return <th key={key}>{prop}</th>;
                      })}
                    </tr>
                  </thead>
                  <tbody>
                    {autores.map((autor, index) => (
                      <tr key={index}>
                        <td>{autor.nombre}</td>
                        <td>{autor.apellido}</td>
                        <td>{autor.fechaNacimiento}</td>
                        <td>
                          <button onClick={() => obtenerAutorPorId(autor.id)}>Obtener por ID</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default RegularTables;
