import React, { useState, useEffect } from 'react';
import { getCupones } from '../services/apiCupones';
import {
  Card,
  CardBody,
  CardHeader,
  Table,
  Row,
  Col,
  Spinner
} from 'reactstrap';
import PanelHeader from 'components/PanelHeader/PanelHeader';

function TablaCupones() {
  const [cupones, setCupones] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await getCupones();
        console.log('Datos recibidos:', response); // Verifica qué datos se están recibiendo
        if (response.isSuccess) {
          setCupones(response.result);
        } else {
          console.error('Error al obtener cupones:', response.message);
        }
        setLoading(false);
      } catch (error) {
        console.error('Error al obtener cupones:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="text-center">
        <Spinner color="primary" />
        <p className="mt-2">Cargando cupones...</p>
      </div>
    );
  }

  if (!Array.isArray(cupones) || cupones.length === 0) {
    return <p>No se encontraron cupones.</p>;
  }

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col xs={12}>
            <Card>
              <CardHeader>
                <h4 className="title">Cupones</h4>
              </CardHeader>
              <CardBody>
                <Table responsive>
                  <thead className="text-primary">
                    <tr>
                      <th>Código del Cupón</th>
                      <th>Porcentaje de Descuento (%)</th>
                      <th>Descuento Mínimo</th>
                    </tr>
                  </thead>
                  <tbody>
                    {cupones.map((cupon, index) => (
                      <tr key={index}>
                        <td>{cupon.cuponCode}</td>
                        <td>{cupon.porcentajeDescuento}</td>
                        <td>{cupon.descuentoMinimo}</td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default TablaCupones;
