import React, { useState, useEffect, useRef } from "react";
import { obtenerTodoElCarrito, eliminarArticuloDelCarrito } from "../services/apiCarrito";
import NotificationAlert from "react-notification-alert";
import { Row, Col, Card, CardBody, CardTitle, CardSubtitle, Button } from "reactstrap";
import PanelHeader from "components/PanelHeader/PanelHeader.js";

const IVA_RATE = 0.16;

function CarritoCompras() {
  const [carrito, setCarrito] = useState([]);
  const [totalDescuento, setTotalDescuento] = useState(0);
  const [precioFinal, setPrecioFinal] = useState(0);
  const notificationAlert = useRef();

  useEffect(() => {
    const fetchCarrito = async () => {
      try {
        const carritoData = await obtenerTodoElCarrito();
        setCarrito(carritoData);
        calcularDetalles(carritoData);
      } catch (error) {
        console.error("Error al obtener el carrito:", error);
        notify('danger', "Error al obtener el carrito.");
      }
    };

    fetchCarrito();
  }, []);

  const calcularDetalles = (carritoData) => {
    let subtotal = 0;
    let descuento = 0;

    carritoData.forEach((item) => {
      const precio = item.precio || 0; // Default to 0 if undefined
      const cantidad = item.cantidad || 1; // Default to 1 if undefined
      
      subtotal += precio * cantidad;

      // Aplicar descuento si hay cupón
      if (item.cupón) {
        descuento = subtotal * (item.cupón / 100);
      }
    });

    const precioConIVA = subtotal * (1 + IVA_RATE);
    setTotalDescuento(descuento);
    setPrecioFinal(precioConIVA - descuento);
  };

  const handleEliminarArticulo = async (carritoSesionId, productoId) => {
    try {
      await eliminarArticuloDelCarrito(carritoSesionId, productoId);
      // Actualizar el carrito después de eliminar el artículo
      const updatedCarrito = carrito.filter(item => item.productoId !== productoId);
      setCarrito(updatedCarrito);
      calcularDetalles(updatedCarrito);
      notify('success', "Artículo eliminado del carrito con éxito.");
    } catch (error) {
      console.error("Error al eliminar el artículo del carrito:", error);
      notify('danger', "Error al eliminar el artículo del carrito.");
    }
  };

  const notify = (type, message) => {
    const options = {
      place: 'tr',
      message: <div>{message}</div>,
      type: type,
      icon: "now-ui-icons ui-1_bell-53",
      autoDismiss: 7
    };
    notificationAlert.current.notificationAlert(options);
  };

  const handleConfirmarPedido = () => {
    // Lógica para confirmar el pedido
    notify('success', "Pedido confirmado con éxito.");
  };

  const handleCancelarPedido = () => {
    // Lógica para cancelar el pedido
    notify('info', "Pedido cancelado.");
  };

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <NotificationAlert ref={notificationAlert} />
        <Row>
          {carrito.length === 0 ? (
            <p>El carrito está vacío.</p>
          ) : (
            carrito.map((item) => (
              <Col md={12} key={item.productoId || Math.random()}>
                <Card>
                  <CardBody>
                    <CardTitle tag="h5">{item.ProductoSeleccionado || "Producto Desconocido"}</CardTitle>
                    <CardSubtitle tag="h6" className="mb-2 text-muted">
                      Precio Unitario: ${ (item.precio || 0).toFixed(2) }
                    </CardSubtitle>
                    <p>Cantidad: { item.cantidad || 1 }</p>
                    <p>Subtotal: ${ ((item.precio || 0) * (item.cantidad || 1)).toFixed(2) }</p>
                    <Button color="danger" onClick={() => handleEliminarArticulo(item.carritoSesionId, item.productoId)}>Eliminar</Button>
                  </CardBody>
                </Card>
              </Col>
            ))
          )}
        </Row>
        <div className="mt-4">
          <h4>Detalle del Pedido</h4>
          <p>Subtotal: ${ carrito.reduce((acc, item) => acc + ((item.precio || 0) * (item.cantidad || 1)), 0).toFixed(2) }</p>
          <p>Descuento: ${ totalDescuento.toFixed(2) }</p>
          <p>Precio Final con IVA (16%): ${ precioFinal.toFixed(2) }</p>
          <Button color="success" onClick={handleConfirmarPedido}>Confirmar Pedido</Button>
          <Button color="secondary" onClick={handleCancelarPedido} className="ml-2">Cancelar Pedido</Button>
        </div>
      </div>
    </>
  );
}

export default CarritoCompras;
