import axios from 'axios';

const API_CARRITO_URL = 'https://localhost:7194/api/CarritoCompras';
const API_AUTOR_URL = 'https://localhost:7114/api/Autor';

export const crearCarrito = async (data) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/Crear`, data);
    return response.data;
  } catch (error) {
    console.error('Error al crear el carrito:', error);
    throw error;
  }
};

export const obtenerCarritoPorId = async (id) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error al obtener carrito por ID:', error);
    throw error;
  }
};

export const obtenerTodoElCarrito = async () => {
  try {
    const responseCarrito = await axios.get(API_CARRITO_URL);
    const carritos = responseCarrito.data;

    const autores = await getAutores();
    const autoresDic = {};
    autores.forEach(autor => {
      autoresDic[autor.autorLibroGuid] = {
        nombre: `${autor.nombre} ${autor.apellido}`,
        imagen: autor.imagen // Base64 image
      };
    });

    const carritosConDetalles = carritos.map(carrito => {
      const listaDeProductosConDetalles = carrito.listaDeProductos.map(producto => {
        const autor = autoresDic[producto.autorLibro] || { nombre: 'Autor desconocido', imagen: null };
        return {
          ...producto,
          autorNombre: autor.nombre,
          autorImagen: autor.imagen
        };
      });

      return {
        ...carrito,
        listaDeProductos: listaDeProductosConDetalles
      };
    });

    return carritosConDetalles;
  } catch (error) {
    console.error('Error al obtener los carritos:', error);
    throw error;
  }
};

export const eliminarArticuloDelCarrito = async (carritoSesionId, productoId) => {
  try {
    const response = await axios.delete(`${API_BASE_URL}/Eliminar/${carritoSesionId}/${productoId}`);
    return response.data;
  } catch (error) {
    console.error('Error al eliminar el artículo del carrito:', error);
    throw error;
  }
};
