import axios from 'axios';

const API_CARRITO_URL = 'https://localhost:7194/api/CarritoCompras';
const API_LIBRO_URL = 'https://localhost:7225/api/LibroMaterial';

export const obtenerCarrito = async (id) => {
  try {
    const response = await axios.get(`${API_CARRITO_URL}/${id}`);
    return response.data;
  } catch (error) {
    console.error("Error al obtener el carrito:", error);
    throw error;
  }
};

export const agregarAlCarrito = async (libroId) => {
  try {
    const response = await axios.post(API_CARRITO_URL, {
      fechaCreacionSesion: new Date().toISOString(),
      productoLista: [libroId],
    });
    return response.data;
  } catch (error) {
    console.error("Error al agregar al carrito:", error);
    throw error;
  }
};
