import React, { useState, useEffect } from 'react';
import { getCupones, getCuponPorId, getCuponPorCodigo } from '../services/apiCupones';
import {
  Card,
  CardBody,
  CardHeader,
  Table,
  Row,
  Col,
  Spinner,
  Input,
  InputGroup,
  InputGroupAddon,
  Button
} from 'reactstrap';
import PanelHeader from 'components/PanelHeader/PanelHeader';

function TablaCupones() {
  const [cupones, setCupones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await getCupones();
        if (response.isSuccess) {
          setCupones(response.result);
        } else {
          console.error('Error al obtener cupones:', response.message);
        }
        setLoading(false);
      } catch (error) {
        console.error('Error al obtener cupones:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = async () => {
    setLoading(true);
    try {
      let searchResult;
      if (!isNaN(searchTerm)) { // Si es un número, buscar por ID
        searchResult = await getCuponPorId(searchTerm);
      } else { // Si no, buscar por código de cupón
        searchResult = await getCuponPorCodigo(searchTerm);
      }
      if (searchResult.isSuccess) {
        setCupones([searchResult.result]);
      } else {
        console.error('Error al buscar cupón:', searchResult.message);
      }
    } catch (error) {
      console.error('Error al buscar cupón:', error);
    }
    setLoading(false);
  };
  
  const handleEdit = (cuponId) => {
    // Lógica para editar el cupón
    console.log('Editar cupón con ID:', cuponId);
  };

  const handleAdd = () => {
    // Lógica para agregar un nuevo cupón
    console.log('Agregar nuevo cupón');
  };

  const handleClear = async () => {
    setSearchTerm(''); // Limpiar el término de búsqueda
    setLoading(true);
    try {
      const response = await getCupones(); // Obtener la lista completa de nuevo
      if (response.isSuccess) {
        setCupones(response.result);
      } else {
        console.error('Error al obtener cupones:', response.message);
      }
    } catch (error) {
      console.error('Error al obtener cupones:', error);
    }
    setLoading(false);
  };

  if (loading) {
    return (
      <div className="text-center">
        <Spinner color="primary" />
        <p className="mt-2">Cargando cupones...</p>
      </div>
    );
  }

  if (!Array.isArray(cupones) || cupones.length === 0) {
    return <p>No se encontraron cupones.</p>;
  }

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col xs={12}>
            <Card>
              <CardHeader>
                <Row>
                  <Col xs={6}>
                    <h4 className="title">Cupones</h4>
                  </Col>
                  <Col xs={6}>
                    <InputGroup>
                      <Input
                        placeholder="Buscar por ID o Código"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        style={{ borderRadius: '0', borderTopRightRadius: '0', borderBottomRightRadius: '0' }}
                      />
                      <InputGroupAddon addonType="append">
                        <Button color="primary" onClick={handleSearch}>Buscar</Button>
                      </InputGroupAddon>
                    </InputGroup>
                  </Col>
                </Row>
              </CardHeader>
              <CardBody>
                <Table responsive>
                  <thead className="text-primary">
                    <tr>
                      <th>Código del Cupón</th>
                      <th>Porcentaje de Descuento (%)</th>
                      <th>Descuento Mínimo</th>
                      <th>Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    {cupones.map((cupon, index) => (
                      <tr key={index}>
                        <td>{cupon.cuponCode}</td>
                        <td>{cupon.porcentajeDescuento}</td>
                        <td>{cupon.descuentoMinimo}</td>
                        <td>
                          <Button color="info" size="sm" onClick={() => handleEdit(cupon.cuponId)}>Editar</Button>{' '}
                          {/* Agregar más botones de acción según necesites */}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
                <Button color="success" onClick={handleAdd}>Agregar Nuevo</Button>{' '}
                <Button color="secondary" onClick={handleClear}>Limpiar Búsqueda</Button>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default TablaCupones;
