import React, { useState, useEffect, useRef } from "react";
import Axios from 'axios';
import { Row, Col, Card, CardBody, CardTitle, CardSubtitle, CardText, Button } from "reactstrap";
import NotificationAlert from "react-notification-alert";
import PanelHeader from "components/PanelHeader/PanelHeader.js";

const IVA_RATE = 0.16;

const CarritoCompras = () => {
  const [carritos, setCarritos] = useState([]);
  const [totalDescuento, setTotalDescuento] = useState(0);
  const [precioFinal, setPrecioFinal] = useState(0);
  const notificationAlert = useRef();

  useEffect(() => {
    const fetchCarritos = async () => {
      try {
        const response = await Axios.get('https://localhost:7194/api/CarritoCompras/');
        setCarritos(response.data);
        calcularDetalles(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
        notify('danger', 'Error al obtener datos del carrito.');
      }
    };

    fetchCarritos();
  }, []);

  const calcularDetalles = (carritoData) => {
    let subtotal = 0;
    let descuento = 0;

    carritoData.forEach((carrito) => {
      carrito.listaDeProductos.forEach((producto) => {
        const precio = producto.precio || 0;
        const cantidad = producto.cantidad || 0;

        subtotal += precio * cantidad;
        // Aplicar descuento si hay cupón
        if (producto.cupón) {
          descuento = subtotal * (producto.cupón / 100);
        }
      });
    });

    const precioConIVA = subtotal * (1 + IVA_RATE);
    setTotalDescuento(descuento);
    setPrecioFinal(precioConIVA - descuento);
  };

  const calcularTotal = (productos) => {
    if (!productos) return 0;
    return productos.reduce((total, producto) => total + (producto.precio || 0), 0);
  };

  const handleEliminarArticulo = async (carritoSesionId, productoId) => {
    try {
      await Axios.delete(`https://localhost:7194/api/CarritoCompras/Eliminar/${carritoSesionId}/${productoId}`);
      // Actualizar el carrito después de eliminar el artículo
      const updatedCarritos = carritos.map(carrito => ({
        ...carrito,
        listaDeProductos: carrito.listaDeProductos.filter(producto => producto.libroId !== productoId)
      }));
      setCarritos(updatedCarritos);
      calcularDetalles(updatedCarritos);
      notify('success', "Artículo eliminado del carrito con éxito.");
    } catch (error) {
      console.error("Error al eliminar el artículo del carrito:", error);
      notify('danger', "Error al eliminar el artículo del carrito.");
    }
  };

  const notify = (type, message) => {
    const options = {
      place: 'tr',
      message: <div>{message}</div>,
      type: type,
      icon: "now-ui-icons ui-1_bell-53",
      autoDismiss: 7
    };
    notificationAlert.current.notificationAlert(options);
  };

  const handleConfirmarPedido = () => {
    // Lógica para confirmar el pedido
    notify('success', "Pedido confirmado con éxito.");
  };

  const handleCancelarPedido = () => {
    // Lógica para cancelar el pedido
    notify('info', "Pedido cancelado.");
  };

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <NotificationAlert ref={notificationAlert} />
        <Row>
          {carritos.length === 0 ? (
            <p>El carrito está vacío.</p>
          ) : (
            carritos.map((carrito) => (
              <Col md={12} key={carrito.carritoId}>
                <Card className="mb-4">
                  <CardBody>
                    <CardTitle tag="h5">Carrito ID: {carrito.carritoId}</CardTitle>
                    <CardText>Fecha de Creación: {new Date(carrito.fechaCreacionSesion).toLocaleString()}</CardText>
                    <CardText tag="h6">Productos:</CardText>
                    {carrito.listaDeProductos && carrito.listaDeProductos.length > 0 ? (
                      carrito.listaDeProductos.map((producto, index) => (
                        <Row key={producto.libroId + '-' + index} className="mb-2">
                          <Col md={8}>
                            <CardText>Título: {producto.tituloLibro}</CardText>
                            <CardText>Fecha de Publicación: {new Date(producto.fechaPublicacion).toLocaleDateString()}</CardText>
                          </Col>
                          <Col md={4} className="text-right">
                            <CardText>Precio: ${producto.precio ? producto.precio.toFixed(2) : 'Sin precio'}</CardText>
                            <Button color="danger" onClick={() => handleEliminarArticulo(carrito.carritoId, producto.libroId)}>Eliminar</Button>
                          </Col>
                        </Row>
                      ))
                    ) : (
                      <CardText>No hay productos en este carrito.</CardText>
                    )}
                    <CardText tag="h6">Total: ${calcularTotal(carrito.listaDeProductos).toFixed(2)}</CardText>
                  </CardBody>
                </Card>
              </Col>
            ))
          )}
        </Row>
        <div className="mt-4">
          <h4>Detalle del Pedido</h4>
          <p>Subtotal: ${carritos.reduce((acc, carrito) => acc + calcularTotal(carrito.listaDeProductos), 0).toFixed(2)}</p>
          <p>Descuento: ${totalDescuento.toFixed(2)}</p>
          <p>Precio Final con IVA (16%): ${precioFinal.toFixed(2)}</p>
          <Button color="success" onClick={handleConfirmarPedido}>Confirmar Pedido</Button>
          <Button color="secondary" onClick={handleCancelarPedido} className="ml-2">Cancelar Pedido</Button>
        </div>
      </div>
    </>
  );
};

export default CarritoCompras;
