import axios from 'axios'; 

const API_LIBRO_URL = 'https://localhost:7225/api/LibroMaterial'; 
const API_AUTOR_URL = 'https://localhost:7114/api/Autor'; 

export const getLibros = async () => {
  try {
    const response = await axios.get(API_LIBRO_URL); 
    return response.data; 
  } catch (error) {
    console.error('Error al obtener libros:', error); 
    throw error; 
  }
};

export const getLibroPorId = async (id) => {
  try {
    const response = await axios.get(`${API_LIBRO_URL}/${id}`); 
    return response.data; 
  } catch (error) {
    console.error('Error al obtener libro por ID:', error); 
    throw error; 
  }
};

export const agregarLibro = async (nuevoLibro) => {
  try {
    const response = await axios.post(API_LIBRO_URL, nuevoLibro, {
      headers: {
        'Content-Type': 'application/json'
      }
    });
    return response.data; 
  } catch (error) {
    console.error('Error al agregar libro:', error); 
    throw error;
  }
};

export const getAutores = async () => {
  try {
    const response = await axios.get(API_AUTOR_URL); 
    return response.data; 
  } catch (error) {
    console.error('Error al obtener autores:', error); 
    throw error; 
  }
};
