// api.js
import axios from 'axios';

const API_URL = 'https://localhost:7114/api/Autor';

export const getAutores = async () => {
  try {
    const response = await axios.get(API_URL);
    return response.data;
  } catch (error) {
    console.error('Error al obtener autores:', error);
    throw error;
  }
};

export const getAutorPorId = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error al obtener autor por ID:', error);
    throw error;
  }
};

export const agregarAutor = async (nuevoAutor, imagen) => {
  try {
    const formData = new FormData();
    formData.append('nombre', nuevoAutor.nombre);
    formData.append('apellido', nuevoAutor.apellido);
    formData.append('fechaNacimiento', nuevoAutor.fechaNacimiento);
    formData.append('imagen', imagen);

    const response = await axios.post(API_URL, formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error al agregar autor:', error);
    throw error;
  }
};
