import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardBody, FormGroup, Form, Input, Row, Col, Button } from 'reactstrap';
import { getCuponPorId, actualizarCupon } from '../services/apiCupones';
import { useNavigate } from 'react-router-dom';
import PanelHeader from 'components/PanelHeader/PanelHeader';

const EditarCupon = () => {
    const navigate = useNavigate();

    const [cupon, setCupon] = useState({
        id: '',
        codigo: '',
        descripcion: '',
        descuento: 0,
        // Añade aquí más campos según tu estructura de datos del cupón
    });

    useEffect(() => {
        // Aquí deberías obtener el ID del cupón a editar desde tus props, por ejemplo usando useParams
        const id = 'ID_DEL_CUPON_A_EDITAR';

        // Llama a la función para obtener el cupón por su ID
        getCuponPorId(id)
            .then(response => {
                // Actualiza el estado del cupón con los datos obtenidos
                setCupon(response);
            })
            .catch(error => {
                console.error('Error al obtener el cupón:', error);
            });
    }, []);

    const handleChange = e => {
        const { name, value } = e.target;
        setCupon(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = e => {
        e.preventDefault();
        actualizarCupon(cupon)
            .then(response => {
                console.log('Cupón actualizado exitosamente:', response);
                navigate('/lista-cupones'); // Navegar a la lista de cupones después de guardar
            })
            .catch(error => {
                console.error('Error al actualizar el cupón:', error);
            });
    };

    return (
        <>
            <PanelHeader size="sm" />
            <div className="content">
                <Row>
                    <Col md="8">
                        <Card>
                            <CardHeader>
                                <h5 className="title">Editar Cupón</h5>
                            </CardHeader>
                            <CardBody>
                                <Form onSubmit={handleSubmit}>
                                    <Row>
                                        <Col className="pr-md-1" md="5">
                                            <FormGroup>
                                                <label>Código</label>
                                                <Input
                                                    type="text"
                                                    name="codigo"
                                                    value={cupon.codigo}
                                                    onChange={handleChange}
                                                    required
                                                />
                                            </FormGroup>
                                        </Col>
                                        <Col className="px-md-1" md="7">
                                            <FormGroup>
                                                <label>Descripción</label>
                                                <Input
                                                    type="text"
                                                    name="descripcion"
                                                    value={cupon.descripcion}
                                                    onChange={handleChange}
                                                    required
                                                />
                                            </FormGroup>
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col className="pr-md-1" md="6">
                                            <FormGroup>
                                                <label>Descuento</label>
                                                <Input
                                                    type="number"
                                                    name="descuento"
                                                    value={cupon.descuento}
                                                    onChange={handleChange}
                                                    required
                                                />
                                            </FormGroup>
                                        </Col>
                                    </Row>
                                    <Button className="btn-fill" color="primary" type="submit">
                                        Guardar Cambios
                                    </Button>
                                </Form>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </div>
        </>
    );
};

export default EditarCupon;
