import React, { useState } from "react";
import { Card, CardHeader, CardBody, FormGroup, Form, Input, Row, Col, Button, Alert } from "reactstrap"; // Importación de componentes de Reactstrap
import PanelHeader from "components/PanelHeader/PanelHeader.js"; // Importación de un componente personalizado
import { agregarLibro } from "../service/apiLibro.js"; // Importación de la función agregarLibro desde el servicio API

function AgregarLibro() {
  // Definición de estados para manejar el formulario y mensajes de alerta
  const [titulo, setTitulo] = useState(""); // Estado para el título del libro
  const [autor, setAutor] = useState(""); // Estado para el autor del libro
  const [fechaPublicacion, setFechaPublicacion] = useState(""); // Estado para la fecha de publicación del libro
  const [imagen, setImagen] = useState(null); // Estado para la imagen del libro
  const [imagenPreview, setImagenPreview] = useState(null); // Estado para la vista previa de la imagen
  const [alertVisible, setAlertVisible] = useState(false); // Estado para la visibilidad de la alerta
  const [alertMessage, setAlertMessage] = useState(""); // Estado para el mensaje de la alerta
  const [alertColor, setAlertColor] = useState("success"); // Estado para el color de la alerta

  // Función que se ejecuta al enviar el formulario
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevenir el comportamiento predeterminado del formulario

    try {
      // Crear un objeto con los datos del nuevo libro
      const nuevoLibro = { titulo, autor, fechaPublicacion };
      // Llamar a la función agregarLibro del servicio API para agregar el nuevo libro
      const respuesta = await agregarLibro(nuevoLibro, imagen);

      console.log("Libro agregado:", respuesta);

      // Reiniciar los valores del formulario y mostrar un mensaje de éxito
      setTitulo("");
      setAutor("");
      setFechaPublicacion("");
      setImagen(null);
      setImagenPreview(null);

      setAlertMessage("Libro agregado exitosamente");
      setAlertColor("success");
      setAlertVisible(true);
    } catch (error) {
      // En caso de error, mostrar un mensaje de error
      console.error("Error al agregar libro:", error);

      setAlertMessage("Error al agregar libro");
      setAlertColor("danger");
      setAlertVisible(true);
    }
  };

  // Función que se ejecuta al cambiar la imagen seleccionada
  const handleImagenChange = (e) => {
    const selectedImagen = e.target.files[0]; // Obtener la imagen seleccionada
    setImagen(selectedImagen); // Actualizar el estado de la imagen

    // Crear un objeto FileReader para leer la imagen y mostrar una vista previa
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagenPreview(reader.result); // Actualizar el estado de la vista previa de la imagen
    };
    reader.readAsDataURL(selectedImagen); // Leer la imagen como una URL de datos
  };

  // Renderizado del componente
  return (
    <>
      <PanelHeader size="sm" /> {/* Encabezado del panel */}
      <div className="content">
        <Row>
          <Col md="8">
            <Card>
              <CardHeader>
                <h5 className="title">Agregar Libro</h5> {/* Título del formulario */}
              </CardHeader>
              <CardBody>
                {/* Alerta para mostrar mensajes de éxito o error */}
                {alertVisible && (
                  <Alert color={alertColor} isOpen={alertVisible} toggle={() => setAlertVisible(false)}>
                    <span>
                      <b>{alertColor === "success" ? "Success - " : "Danger - "}</b>
                      {alertMessage}
                    </span>
                  </Alert>
                )}
                {/* Formulario para agregar un nuevo libro */}
                <Form onSubmit={handleSubmit}>
                  <Row>
                    {/* Campo para ingresar el título del libro */}
                    <Col className="pr-1" md="6">
                      <FormGroup>
                        <label>Título</label>
                        <Input
                          value={titulo}
                          onChange={(e) => setTitulo(e.target.value)}
                          placeholder="Título"
                          type="text"
                          required
                        />
                      </FormGroup>
                    </Col>
                    {/* Campo para ingresar el autor del libro */}
                    <Col className="pl-1" md="6">
                      <FormGroup>
                        <label>Autor</label>
                        <Input
                          value={autor}
                          onChange={(e) => setAutor(e.target.value)}
                          placeholder="Autor"
                          type="text"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  {/* Campo para ingresar la fecha de publicación del libro */}
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <label>Fecha de Publicación</label>
                        <Input
                          value={fechaPublicacion}
                          onChange={(e) => setFechaPublicacion(e.target.value)}
                          placeholder="Fecha de Publicación"
                          type="date"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  {/* Campo para seleccionar una imagen */}
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <label>Imagen</label>
                        <Input
                          type="file"
                          accept="image/*"
                          onChange={handleImagenChange}
                          required
                        />
                      </FormGroup>
                      {/* Vista previa de la imagen seleccionada */}
                      {imagenPreview && (
                        <img src={imagenPreview} alt="Vista previa de la imagen" style={{ maxWidth: '100%', maxHeight: '300px' }} />
                      )}
                    </Col>
                  </Row>
                  {/* Botón para enviar el formulario */}
                  <Button type="submit" color="primary">Agregar Libro</Button>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default AgregarLibro;
