import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardBody, FormGroup, Form, Input, Row, Col, Button, Alert } from 'reactstrap';
import PanelHeader from 'components/PanelHeader/PanelHeader';
import { actualizarCupon, getCuponPorId } from '../services/apiCupones';
import { Link } from 'react-router-dom';

function EditarCupon({ match }) {
  const cuponId = match.params.id; // Obtén el ID del cupón desde los parámetros de la URL

  const [cupon, setCupon] = useState({
    cuponCode: '',
    porcentajeDescuento: '',
    descuentoMinimo: ''
  });
  const [alertVisible, setAlertVisible] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertColor, setAlertColor] = useState('success');

  useEffect(() => {
    const fetchCupon = async () => {
      try {
        // Obtener datos del cupón por su ID desde la API
        const response = await getCuponPorId(cuponId);
        if (response.isSuccess) {
          const cuponData = response.result;
          setCupon(cuponData);
        } else {
          console.error('Error al obtener datos del cupón:', response.message);
          setAlertMessage('Error al obtener datos del cupón');
          setAlertColor('danger');
          setAlertVisible(true);
        }
      } catch (error) {
        console.error('Error al obtener datos del cupón:', error);
        setAlertMessage('Error al obtener datos del cupón');
        setAlertColor('danger');
        setAlertVisible(true);
      }
    };

    fetchCupon();
  }, [cuponId]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const cuponActualizado = {
        cuponId,
        cuponCode: cupon.cuponCode,
        porcentajeDescuento: cupon.porcentajeDescuento,
        descuentoMinimo: cupon.descuentoMinimo
      };

      const respuesta = await actualizarCupon(cuponActualizado);
      console.log('Cupón actualizado:', respuesta);

      setAlertMessage('Cupón actualizado exitosamente');
      setAlertColor('success');
      setAlertVisible(true);
    } catch (error) {
      console.error('Error al actualizar cupón:', error);

      setAlertMessage('Error al actualizar cupón');
      setAlertColor('danger');
      setAlertVisible(true);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCupon(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col md="8">
            <Card>
              <CardHeader>
                <Row>
                  <Col>
                    <h5 className="title">Editar Cupón</h5>
                  </Col>
                  <Col className="text-right">
                    <Link to="/admin/lista-cupones">
                      <Button color="secondary">
                        Regresar a la lista
                      </Button>
                    </Link>
                  </Col>
                </Row>
              </CardHeader>
              <CardBody>
                {alertVisible && (
                  <Alert color={alertColor} isOpen={alertVisible} toggle={() => setAlertVisible(false)}>
                    <span>
                      <b>{alertColor === 'success' ? 'Éxito - ' : 'Error - '}</b>
                      {alertMessage}
                    </span>
                  </Alert>
                )}
                <Form onSubmit={handleSubmit}>
                  <Row>
                    <Col className="pr-1" md="6">
                      <FormGroup>
                        <label>Código</label>
                        <Input
                          name="cuponCode"
                          value={cupon.cuponCode}
                          onChange={handleChange}
                          placeholder="Código"
                          type="text"
                          required
                        />
                      </FormGroup>
                    </Col>
                    <Col className="pl-1" md="6">
                      <FormGroup>
                        <label>Porcentaje de Descuento</label>
                        <Input
                          name="porcentajeDescuento"
                          value={cupon.porcentajeDescuento}
                          onChange={handleChange}
                          placeholder="Porcentaje de Descuento"
                          type="number"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <label>Descuento Mínimo</label>
                        <Input
                          name="descuentoMinimo"
                          value={cupon.descuentoMinimo}
                          onChange={handleChange}
                          placeholder="Descuento Mínimo"
                          type="number"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Button type="submit" color="primary">
                    Guardar Cambios
                  </Button>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default EditarCupon;
