import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardBody, FormGroup, Form, Input, Row, Col, Button } from 'reactstrap';
import { getCuponPorId, actualizarCupon } from '../services/apiCupones';
import { useNavigate, Link } from 'react-router-dom';
import PanelHeader from 'components/PanelHeader/PanelHeader';

const EditarCupon = ({ id }) => {
    const navigate = useNavigate();

    const [cupon, setCupon] = useState({
        cuponcode: '',
        porcentajeDescuento: 0,
        DescuentoMínimo: 0,
    });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCupon = async () => {
            try {
                const response = await getCuponPorId(id);
                setCupon({
                    cuponcode: response.cuponcode,
                    porcentajeDescuento: response.porcentajeDescuento,
                    DescuentoMínimo: response.DescuentoMínimo,
                });
                setLoading(false);
            } catch (error) {
                console.error('Error al obtener el cupón por ID:', error);
                setLoading(false);
            }
        };

        if (id) { // Verifica que id esté definido antes de hacer la solicitud
            fetchCupon();
        }
    }, [id]);

    const handleChange = e => {
        const { name, value } = e.target;
        setCupon(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = e => {
        e.preventDefault();
        actualizarCupon({
            cuponcode: cupon.cuponcode,
            porcentajeDescuento: cupon.porcentajeDescuento,
            DescuentoMínimo: cupon.DescuentoMínimo,
        })
        .then(response => {
            console.log('Cupón actualizado exitosamente:', response.data);
            navigate('/admin/lista-cupones'); // Navegar a la lista de cupones después de guardar
        })
        .catch(error => {
            console.error('Error al actualizar el cupón:', error);
        });
    };

    return (
        <>
            <PanelHeader size="sm" />
            <div className="content">
                <Row>
                    <Col md="8">
                        <Card>
                            <CardHeader>
                                <h5 className="title">Editar Cupón</h5>
                                <Link to="/admin/lista-cupones" className="btn btn-secondary btn-round btn-sm">
                                    Regresar a la Lista
                                </Link>
                            </CardHeader>
                            <CardBody>
                                <Form onSubmit={handleSubmit}>
                                    <Row>
                                        <Col className="pr-md-1" md="5">
                                            <FormGroup>
                                                <label>Código de Cupón</label>
                                                <Input
                                                    type="text"
                                                    name="cuponcode"
                                                    value={cupon.cuponcode}
                                                    onChange={handleChange}
                                                    required
                                                />
                                            </FormGroup>
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col className="pr-md-1" md="4">
                                            <FormGroup>
                                                <label>Porcentaje de Descuento</label>
                                                <Input
                                                    type="number"
                                                    name="porcentajeDescuento"
                                                    value={cupon.porcentajeDescuento}
                                                    onChange={handleChange}
                                                />
                                            </FormGroup>
                                        </Col>
                                        <Col className="px-md-1" md="4">
                                            <FormGroup>
                                                <label>Descuento Mínimo</label>
                                                <Input
                                                    type="number"
                                                    name="DescuentoMínimo"
                                                    value={cupon.DescuentoMínimo}
                                                    onChange={handleChange}
                                                />
                                            </FormGroup>
                                        </Col>
                                    </Row>
                                    <Button className="btn-fill" color="primary" type="submit">
                                        Guardar Cambios
                                    </Button>
                                </Form>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </div>
        </>
    );
};

export default EditarCupon;
