import React, { useState, useEffect } from 'react';
import { getCupones, getCuponPorId, getCuponPorCodigo } from '../services/apiCupones';
import {
  Card,
  CardBody,
  CardHeader,
  Table,
  Row,
  Col,
  Spinner,
  Input,
  Button
} from 'reactstrap';
import PanelHeader from 'components/PanelHeader/PanelHeader';

function ListaCupones() {
  const [cupones, setCupones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchId, setSearchId] = useState('');
  const [searchCodigo, setSearchCodigo] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await getCupones();
        if (response.isSuccess) {
          setCupones(response.result);
        } else {
          console.error('Error al obtener cupones:', response.message);
        }
        setLoading(false);
      } catch (error) {
        console.error('Error al obtener cupones:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearchById = async () => {
    if (!searchId) return; // No hacer la solicitud si el campo de búsqueda está vacío
    try {
      const cupon = await getCuponPorId(searchId);
      console.log('Cupón encontrado por ID:', cupon);
      // Aquí puedes manejar la lógica para mostrar el cupón encontrado
    } catch (error) {
      console.error('Error al buscar cupón por ID:', error);
      // Aquí puedes manejar el error, como mostrar un mensaje al usuario
    }
  };

  const handleSearchByCodigo = async () => {
    if (!searchCodigo) return; // No hacer la solicitud si el campo de búsqueda está vacío
    try {
      const cupon = await getCuponPorCodigo(searchCodigo);
      console.log('Cupón encontrado por código:', cupon);
      // Aquí puedes manejar la lógica para mostrar el cupón encontrado
    } catch (error) {
      console.error('Error al buscar cupón por código:', error);
      // Aquí puedes manejar el error, como mostrar un mensaje al usuario
    }
  };

  if (loading) {
    return (
      <div className="text-center">
        <Spinner color="primary" />
        <p className="mt-2">Cargando cupones...</p>
      </div>
    );
  }

  if (!Array.isArray(cupones) || cupones.length === 0) {
    return <p>No se encontraron cupones.</p>;
  }

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col xs={12}>
            <Card>
              <CardHeader>
                <h4 className="title">Cupones</h4>
              </CardHeader>
              <CardBody>
                <Table responsive>
                  <thead className="text-primary">
                    <tr>
                      <th>Código del Cupón</th>
                      <th>Porcentaje de Descuento (%)</th>
                      <th>Descuento Mínimo</th>
                      <th>Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    {cupones.map((cupon, index) => (
                      <tr key={index}>
                        <td>{cupon.cuponCode}</td>
                        <td>{cupon.porcentajeDescuento}</td>
                        <td>{cupon.descuentoMinimo}</td>
                        <td>
                          <Button color="info" onClick={() => handleEdit(cupon)}>Editar</Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
        <Row className="mt-3">
          <Col md={6}>
            <h4>Buscar Cupón por ID</h4>
            <Input
              type="text"
              placeholder="ID del Cupón"
              value={searchId}
              onChange={(e) => setSearchId(e.target.value)}
            />
            <Button color="primary" className="ml-2" onClick={handleSearchById}>Buscar</Button>
          </Col>
          <Col md={6}>
            <h4>Buscar Cupón por Código</h4>
            <Input
              type="text"
              placeholder="Código del Cupón"
              value={searchCodigo}
              onChange={(e) => setSearchCodigo(e.target.value)}
            />
            <Button color="primary" className="ml-2" onClick={handleSearchByCodigo}>Buscar</Button>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default ListaCupones;
