import React, { useState, useEffect } from "react";
import { obtenerCarrito } from "../services/apiCarrito";
import { Row, Col, Card, CardBody, CardTitle, CardSubtitle, CardImg } from "reactstrap";
import PanelHeader from "components/PanelHeader/PanelHeader.js"; // Asegúrate de que la ruta sea correcta

function CarritoCompras({ carritoId }) {
  const [carrito, setCarrito] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCarrito = async () => {
      try {
        const carritoData = await obtenerCarrito(carritoId);
        setCarrito(carritoData);
      } catch (error) {
        console.error("Error al obtener el carrito:", error);
        setError("Error al obtener el carrito. Por favor, intenta nuevamente.");
      }
    };

    fetchCarrito();
  }, [carritoId]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!carrito) {
    return <div>Cargando carrito...</div>;
  }

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          {carrito.listaDeProductos.length === 0 ? (
            <Col>
              <p>No hay productos en el carrito.</p>
            </Col>
          ) : (
            carrito.listaDeProductos.map((producto, index) => (
              <Col md={4} key={index}>
                <Card>
                  {producto.autorImagen && (
                    <CardImg
                      top
                      width="100%"
                      src={`data:image/jpeg;base64,${producto.autorImagen}`}
                      alt={producto.autorNombre}
                      style={{ height: '250px', objectFit: 'cover' }}
                    />
                  )}
                  <CardBody>
                    <CardTitle tag="h5">{producto.tituloLibro}</CardTitle>
                    <CardSubtitle tag="h6" className="mb-2 text-muted">
                      {producto.autorNombre || "Autor desconocido"}
                    </CardSubtitle>
                    <p>Fecha de Publicación: {new Date(producto.fechaPublicacion).toLocaleDateString()}</p>
                  </CardBody>
                </Card>
              </Col>
            ))
          )}
        </Row>
      </div>
    </>
  );
}

export default CarritoCompras;
