import React, { useState } from "react";
import { Card, CardHeader, CardBody, FormGroup, Form, Input, Row, Col, Button, Alert } from "reactstrap";
import PanelHeader from "components/PanelHeader/PanelHeader.js";
import { agregarAutor } from "../service/api.js"; // Importa la función para agregar autor

function User() {
  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState("");
  const [fechaNacimiento, setFechaNacimiento] = useState("");
  const [imagen, setImagen] = useState(null); // Estado para la imagen
  const [alertVisible, setAlertVisible] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [alertColor, setAlertColor] = useState("success");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Envía los datos del nuevo autor a la API junto con la imagen
      const nuevoAutor = { nombre, apellido, fechaNacimiento };
      const respuesta = await agregarAutor(nuevoAutor, imagen);

      // Lógica para manejar la respuesta de la API
      console.log("Autor agregado:", respuesta);

      // Limpiar los campos después de agregar el autor
      setNombre("");
      setApellido("");
      setFechaNacimiento("");
      setImagen(null);

      // Mostrar alerta de éxito
      setAlertMessage("Autor agregado exitosamente");
      setAlertColor("success");
      setAlertVisible(true);
    } catch (error) {
      console.error("Error al agregar autor:", error);

      // Mostrar alerta de error
      setAlertMessage("Error al agregar autor");
      setAlertColor("danger");
      setAlertVisible(true);
    }
  };

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col md="8">
            <Card>
              <CardHeader>
                <h5 className="title">Agregar Autor</h5>
              </CardHeader>
              <CardBody>
                {alertVisible && (
                  <Alert color={alertColor} isOpen={alertVisible} toggle={() => setAlertVisible(false)}>
                    <span>
                      <b>{alertColor === "success" ? "Success - " : "Danger - "}</b>
                      {alertMessage}
                    </span>
                  </Alert>
                )}
                <Form onSubmit={handleSubmit}>
                  <Row>
                    <Col className="pr-1" md="6">
                      <FormGroup>
                        <label>Nombre</label>
                        <Input
                          value={nombre}
                          onChange={(e) => setNombre(e.target.value)}
                          placeholder="Nombre"
                          type="text"
                          required
                        />
                      </FormGroup>
                    </Col>
                    <Col className="pl-1" md="6">
                      <FormGroup>
                        <label>Apellido</label>
                        <Input
                          value={apellido}
                          onChange={(e) => setApellido(e.target.value)}
                          placeholder="Apellido"
                          type="text"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <label>Fecha de Nacimiento</label>
                        <Input
                          value={fechaNacimiento}
                          onChange={(e) => setFechaNacimiento(e.target.value)}
                          placeholder="Fecha de Nacimiento"
                          type="date"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <label>Imagen</label>
                        <Input
                          type="file"
                          accept="image/*"
                          onChange={(e) => setImagen(e.target.files[0])}
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Button type="submit" color="primary">Agregar Autor</Button>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default User;
