import React, { useState, useEffect } from "react";
import { getCupones } from "../services/apiCupones"; // Importación de funciones del servicio API de cupones
import { Card, CardBody, CardHeader, Table, Row, Col } from "reactstrap"; // Importación de componentes de Reactstrap
import PanelHeader from "components/PanelHeader/PanelHeader"; // Importación de un componente personalizado

function TablaCupones() {
  const [cupones, setCupones] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Obtener la lista de cupones usando la función getCupones del servicio API
        const dataCupones = await getCupones();
        setCupones(dataCupones); // Actualizar el estado con la lista de cupones obtenida
      } catch (error) {
        console.error("Error al obtener cupones:", error); // Manejo de errores si falla la obtención de cupones
      }
    };

    fetchData(); // Llamar a la función fetchData al montar el componente
  }, []);

  // Renderizado del componente
  return (
    <>
      <PanelHeader size="sm" /> {/* Encabezado del panel */}
      <div className="content">
        <Row>
          <Col xs={12}>
            <Card>
              <CardHeader>
                <h4 className="title">Cupones</h4>
              </CardHeader>
              <CardBody>
                {/* Tabla para mostrar la lista de cupones */}
                <Table responsive>
                  <thead className="text-primary">
                    <tr>
                      <th>Código</th> {/* Encabezado de columna para el código del cupón */}
                      <th>Porcentaje de Descuento</th> {/* Encabezado de columna para el porcentaje de descuento */}
                      <th>Descuento Mínimo</th> {/* Encabezado de columna para el descuento mínimo */}
                    </tr>
                  </thead>
                  <tbody>
                    {/* Mapeo de la lista de cupones para mostrar cada cupón en una fila */}
                    {cupones.map((cupon, index) => (
                      <tr key={index}>
                        <td>{cupon.codigo}</td> {/* Celda con el código del cupón */}
                        <td>{cupon.porcentajeDescuento}%</td> {/* Celda con el porcentaje de descuento */}
                        <td>${cupon.descuentoMinimo}</td> {/* Celda con el descuento mínimo */}
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default TablaCupones;
