import React, { useState, useEffect } from "react";
import { obtenerCarrito } from "../services/apiCarrito";
import { Row, Col, Card, CardBody, CardTitle, CardSubtitle, Button } from "reactstrap";
import PanelHeader from "components/PanelHeader/PanelHeader.js";

function Carrito({ carritoId }) {
  const [carrito, setCarrito] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCarrito = async () => {
      try {
        const data = await obtenerCarrito(carritoId);
        if (data.listaDeProductos.length === 0) {
          setError("Tu carrito está vacío.");
        } else {
          setCarrito(data);
        }
      } catch (error) {
        setError("Error al cargar el carrito.");
        console.error(error);
      }
    };

    fetchCarrito();
  }, [carritoId]);

  if (error) {
    return (
      <div className="content">
        <PanelHeader size="sm" />
        <h2>Carrito de Compras</h2>
        <div>{error}</div>
      </div>
    );
  }

  if (!carrito) {
    return (
      <div className="content">
        <PanelHeader size="sm" />
        <h2>Carrito de Compras</h2>
        <div>Cargando...</div>
      </div>
    );
  }

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <h2>Carrito de Compras</h2>
        <Row>
          {carrito.listaDeProductos.map((producto, index) => (
            <Col md={4} key={index}>
              <Card>
                <CardBody>
                  <CardTitle tag="h5">{producto.tituloLibro}</CardTitle>
                  <CardSubtitle tag="h6" className="mb-2 text-muted">
                    {producto.autorLibro || "Autor desconocido"}
                  </CardSubtitle>
                  <p>Fecha de Publicación: {new Date(producto.fechaPublicacion).toLocaleDateString()}</p>
                  <Button color="danger">Eliminar</Button>
                </CardBody>
              </Card>
            </Col>
          ))}
        </Row>
      </div>
    </>
  );
}

export default Carrito;
