import React, { useState } from 'react';
import { getLibroPorGuid } from '../service/apiLibro.js';

import {
  Card,
  CardHeader,
  CardBody,
  Row,
  Col,
  Form,
  Input,
  Button,
  CardTitle,
} from 'reactstrap';

function BuscarLibroPorGuid() {
  const [searchGuid, setSearchGuid] = useState('');
  const [libro, setLibro] = useState(null);
  const [searched, setSearched] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const data = await getLibroPorGuid(searchGuid);
      setLibro(data);
      setSearched(true);
    } catch (error) {
      console.error('Error al obtener libro por GUID:', error);
      setLibro(null);
      setSearched(true);
    }
  };

  return (
    <>
      <div className="content">
        <Row>
          <Col md={6}>
            <Card>
              <CardHeader>
                <h5 className="title">Buscar Libro por GUID</h5>
                <p className="category">Ingresa el GUID del libro</p>
              </CardHeader>
              <CardBody>
                <Form onSubmit={handleSearch}>
                  <Row>
                    <Col md="8">
                      <Input
                        type="text"
                        placeholder="GUID del Libro"
                        value={searchGuid}
                        onChange={(e) => setSearchGuid(e.target.value)}
                      />
                    </Col>
                    <Col md="4">
                      <Button type="submit" color="primary">
                        Buscar
                      </Button>
                    </Col>
                  </Row>
                </Form>
              </CardBody>
            </Card>
          </Col>
          {libro && (
            <Col md="6">
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">{libro.titulo}</CardTitle>
                  <p className="category">{libro.fechaPublicacion}</p>
                </CardHeader>
                <CardBody>
                  <p>Escrito por: {libro.autorNombre}</p>
                </CardBody>
              </Card>
            </Col>
          )}
          {searched && !libro && (
            <Col md={12}>
              <div className="mt-4">
                <p>No se encontró ningún libro con el GUID proporcionado.</p>
              </div>
            </Col>
          )}
        </Row>
      </div>
    </>
  );
}

export default BuscarLibroPorGuid;
