import React, { useState } from "react";
import { getAutorPorId } from "../service/api.js";
import { Card, CardHeader, CardBody, Row, Col, Form, Input, Button } from "reactstrap";
import PanelHeader from "components/PanelHeader/PanelHeader.js";

function Typography() {
  const [searchId, setSearchId] = useState("");
  const [autor, setAutor] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const data = await getAutorPorId(searchId);
      setAutor(data);
    } catch (error) {
      console.error("Error al obtener autor:", error);
      setAutor(null);
    }
  };

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>
                <h5 className="title">Buscar Autor</h5>
                <p className="category">Ingresa el ID del autor</p>
              </CardHeader>
              <CardBody>
                <Form onSubmit={handleSearch}>
                  <Row>
                    <Col md="8">
                      <Input
                        type="text"
                        placeholder="ID del Autor"
                        value={searchId}
                        onChange={(e) => setSearchId(e.target.value)}
                      />
                    </Col>
                    <Col md="4">
                      <Button type="submit" color="primary">Buscar</Button>
                    </Col>
                  </Row>
                </Form>
                {autor && (
                  <div className="mt-4">
                    <h5>Información del Autor</h5>
                    <p><strong>Nombre:</strong> {autor.nombre}</p>
                    <p><strong>Apellido:</strong> {autor.apellido}</p>
                    <p><strong>Fecha de Nacimiento:</strong> {autor.fechaNacimiento}</p>
                    {autor.imagenes && (
                      <div>
                        <h6>Imagen del Autor</h6>
                        <img src={`data:image/jpeg;base64,${autor.imagenes}`} alt="Imagen del autor" style={{ maxWidth: '100%', maxHeight: '300px' }} />
                      </div>
                    )}
                  </div>
                )}
                {!autor && searchId && (
                  <div className="mt-4">
                    <p>No se encontró ningún autor con el ID proporcionado.</p>
                  </div>
                )}
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default Typography;
