import React, { useState, useEffect } from 'react';
import { getCupones } from '../services/apiCupones'; // Importación de funciones del servicio API de cupones
import {
  Card,
  CardBody,
  CardHeader,
  Table,
  Row,
  Col
} from 'reactstrap'; // Importación de componentes de Reactstrap
import PanelHeader from 'components/PanelHeader/PanelHeader'; // Importación de un componente personalizado

function TablaCupones() {
  const [cupones, setCupones] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const dataCupones = await getCupones();
        setCupones(dataCupones);
      } catch (error) {
        console.error('Error al obtener cupones:', error);
      }
    };

    fetchData();
  }, []);

  const formatDate = (dateString) => {
    if (!dateString) return '';

    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = (1 + date.getMonth()).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');

    return `${day}/${month}/${year}`;
  };

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col xs={12}>
            <Card>
              <CardHeader>
                <h4 className="title">Cupones</h4>
              </CardHeader>
              <CardBody>
                <Table responsive>
                  <thead className="text-primary">
                    <tr>
                      <th>Código</th>
                      <th>Descripción</th>
                      <th>Descuento (%)</th>
                      <th>Fecha de Creación</th>
                    </tr>
                  </thead>
                  <tbody>
                    {cupones.map((cupon, index) => (
                      <tr key={index}>
                        <td>{cupon.codigo}</td>
                        <td>{cupon.descripcion}</td>
                        <td>{cupon.descuento}</td>
                        <td>{formatDate(cupon.fechaCreacion)}</td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default TablaCupones;
