import React, { useState } from "react";
import { getLibroPorGuid } from "../service/apiLibro.js"; // Importa la función getLibroPorGuid del servicio API
import {
  Card,
  CardHeader,
  CardBody,
  Row,
  Col,
  Form,
  Input,
  Button,
  CardTitle,
} from "reactstrap"; // Importa componentes de Reactstrap
import PanelHeader from "components/PanelHeader/PanelHeader.js"; // Componente personalizado para el encabezado del panel

function BuscarLibroPorGuid() {
  // Estados para manejar el GUID de búsqueda y la información del libro encontrado
  const [searchGuid, setSearchGuid] = useState(""); // Estado para el GUID de búsqueda
  const [libro, setLibro] = useState(null); // Estado para almacenar la información del libro encontrado
  const [searched, setSearched] = useState(false); // Estado para controlar si se ha realizado una búsqueda

  // Función que se ejecuta al enviar el formulario de búsqueda
  const handleSearch = async (e) => {
    e.preventDefault(); // Prevenir el comportamiento predeterminado del formulario
    try {
      // Llama a la función getLibroPorGuid del servicio API para obtener la información del libro
      const data = await getLibroPorId(searchGuid);
      setLibro(data); // Actualiza el estado con la información del libro encontrado
      setSearched(true); // Indica que se ha realizado una búsqueda
    } catch (error) {
      // En caso de error, muestra un mensaje de error y reinicia la información del libro
      console.error("Error al obtener libro:", error);
      setLibro(null);
      setSearched(true); // Indica que se ha realizado una búsqueda
    }
  };

  // Renderizado del componente
  return (
    <>
      <PanelHeader size="sm" /> {/* Encabezado del panel */}
      <div className="content">
        <Row>
          <Col md={6}>
            <Card>
              <CardHeader>
                <h5 className="title">Buscar Libro por GUID</h5> {/* Título del formulario de búsqueda */}
                <p className="category">Ingresa el GUID del libro</p> {/* Descripción del formulario */}
              </CardHeader>
              <CardBody>
                {/* Formulario para buscar un libro por su GUID */}
                <Form onSubmit={handleSearch}>
                  <Row>
                    <Col md="8">
                      {/* Campo de entrada para ingresar el GUID del libro */}
                      <Input
                        type="text"
                        placeholder="GUID del Libro"
                        value={searchGuid}
                        onChange={(e) => setSearchGuid(e.target.value)}
                      />
                    </Col>
                    <Col md="4">
                      {/* Botón para enviar el formulario de búsqueda */}
                      <Button type="submit" color="primary">
                        Buscar
                      </Button>
                    </Col>
                  </Row>
                </Form>
              </CardBody>
            </Card>
          </Col>
          {libro && (
            <Col md="6">
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">{libro.titulo}</CardTitle>
                  <p className="category">{libro.fechaPublicacion}</p>
                </CardHeader>
                <CardBody>
                  <p>Escrito por: {libro.autor}</p>
                </CardBody>
              </Card>
            </Col>
          )}
          {/* Si no se encuentra ningún libro con el GUID proporcionado después de la búsqueda, mostrar un mensaje */}
          {searched && !libro && (
            <Col md={12}>
              <div className="mt-4">
                <p>No se encontró ningún libro con el GUID proporcionado.</p>
              </div>
            </Col>
          )}
        </Row>
      </div>
    </>
  );
}

export default BuscarLibroPorGuid;
