import React, { useState, useEffect } from 'react';
import { getCupones } from '../services/apiCupones';
import {
  Card,
  CardBody,
  CardHeader,
  Table,
  Row,
  Col,
  Spinner // Importa Spinner para mostrar mientras se carga
} from 'reactstrap';
import PanelHeader from 'components/PanelHeader/PanelHeader';

function TablaCupones() {
  const [cupones, setCupones] = useState(null); // Inicializa como null para indicar carga
  const [loading, setLoading] = useState(true); // Estado para controlar la carga

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await getCupones();
        setCupones(data);
        setLoading(false); // Indica que la carga ha terminado
      } catch (error) {
        console.error('Error al obtener cupones:', error);
        setLoading(false); // Incluso en error, asegúrate de no quedar en estado de carga infinita
      }
    };

    fetchData();
  }, []);

  const formatDate = (dateString) => {
    if (!dateString) return '';

    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = (1 + date.getMonth()).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');

    return `${day}/${month}/${year}`;
  };

  // Muestra un spinner mientras se carga la información
  if (loading) {
    return (
      <div className="text-center">
        <Spinner color="primary" />
        <p className="mt-2">Cargando cupones...</p>
      </div>
    );
  }

  // Si cupones aún es null o undefined, muestra un mensaje de error o carga incompleta
  if (!cupones || cupones.length === 0) {
    return <p>No se encontraron cupones.</p>;
  }

  // Renderiza la tabla si cupones tiene datos
  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col xs={12}>
            <Card>
              <CardHeader>
                <h4 className="title">Cupones</h4>
              </CardHeader>
              <CardBody>
                <Table responsive>
                  <thead className="text-primary">
                    <tr>
                      <th>Código</th>
                      <th>Porcentaje de Descuento (%)</th>
                      <th>Descuento Mínimo</th>
                      <th>Fecha de Creación</th>
                    </tr>
                  </thead>
                  <tbody>
                    {cupones.map((cupon, index) => (
                      <tr key={index}>
                        <td>{cupon.codigo}</td>
                        <td>{cupon.porcentajeDescuento}</td>
                        <td>{cupon.descuentoMinimo}</td>
                        <td>{formatDate(cupon.fechaCreacion)}</td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default TablaCupones;
