import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Row, Col, Card, CardBody, CardTitle, Button, Input, Table } from 'reactstrap';
import { obtenerTodoElCarrito, eliminarArticuloDelCarrito } from '../services/apiCarrito';
import { getCuponPorCodigo, getCupones } from '../services/apiCupones';
import { getAutores } from '../services/apiAutor'; // Asegúrate de importar la función getAutores

const CarritoCompras = () => {
  const [carritos, setCarritos] = useState([]);
  const [cupon, setCupon] = useState('');
  const [descuento, setDescuento] = useState(0);
  const [cuponesValidos, setCuponesValidos] = useState([]);

  useEffect(() => {
    const fetchCarritos = async () => {
      const result = await axios.get('URL_DE_TU_API');
      setCarritos(result.data);
    };

    fetchCarritos();
  }, []);

  const agruparProductos = (productos) => {
    const productosAgrupados = {};

    productos.forEach((producto) => {
      if (productosAgrupados[producto.libroId]) {
        productosAgrupados[producto.libroId].cantidad += 1;
      } else {
        productosAgrupados[producto.libroId] = { ...producto, cantidad: 1 };
      }
    });

    return Object.values(productosAgrupados);
  };

  const aplicarCupon = () => {
    // Lógica para aplicar cupón y calcular descuento
  };

  const productosAgrupados = agruparProductos(
    carritos.flatMap((carrito) => carrito.listaDeProductos)
  );

  const totalCantidad = productosAgrupados.reduce((acc, prod) => acc + prod.cantidad, 0);
  const subtotal = productosAgrupados.reduce((acc, prod) => acc + prod.precio * prod.cantidad, 0);
  const totalImpuestos = subtotal * 0.16;
  const totalConImpuestos = subtotal + totalImpuestos;
  const totalConDescuento = totalConImpuestos * ((100 - descuento) / 100);

  return (
    <>
      <div className="container mt-4">
        <Row>
          <Col md="8">
            <h2>Carrito Registrado</h2>
            {productosAgrupados.map((producto) => (
              <Card className="mb-3" key={producto.libroId}>
                <CardBody>
                  <Row>
                    <Col md="4">
                      <img src={producto.imagenes} alt={producto.tituloLibro} className="img-fluid" />
                    </Col>
                    <Col md="8">
                      <CardTitle tag="h5">{producto.tituloLibro}</CardTitle>
                      <p>Autor: {producto.autorLibro}</p>
                      <p>Fecha de Publicación: {new Date(producto.fechaPublicacion).toLocaleDateString()}</p>
                      <p>Cantidad: {producto.cantidad}</p>
                      <p className="text-success">${producto.precio.toFixed(2)}</p>
                      <Button color="danger">Eliminar</Button>
                    </Col>
                  </Row>
                </CardBody>
              </Card>
            ))}
          </Col>
          <Col md="4">
            <Card>
              <CardBody>
                <CardTitle tag="h5">Detalle del Pedido</CardTitle>
                <Table>
                  <tbody>
                    <tr>
                      <th>Cantidad:</th>
                      <td>{totalCantidad}</td>
                    </tr>
                    <tr>
                      <th>Subtotal:</th>
                      <td>${subtotal.toFixed(2)}</td>
                    </tr>
                    <tr>
                      <th>Descuento:</th>
                      <td>{descuento}%</td>
                    </tr>
                    <tr>
                      <th>Impuestos:</th>
                      <td>${totalImpuestos.toFixed(2)}</td>
                    </tr>
                    <tr>
                      <th>Total:</th>
                      <td>${totalConImpuestos.toFixed(2)}</td>
                    </tr>
                    <tr>
                      <th>Total con descuento:</th>
                      <td>${totalConDescuento.toFixed(2)}</td>
                    </tr>
                  </tbody>
                </Table>
                <Button color="primary">Finalizar Compra</Button>
              </CardBody>
            </Card>
            <div className="mt-4">
              <Input
                type="text"
                value={cupon}
                onChange={(e) => setCupon(e.target.value)}
                placeholder="Ingrese cupón"
              />
              <Button color="success" onClick={aplicarCupon} className="mt-2">Aplicar Cupón</Button>
            </div>
            <div className="mt-4">
              <h5>Cupones Disponibles</h5>
              {cuponesValidos.length > 0 ? (
                <ul>
                  {cuponesValidos.map(cupon => (
                    <li key={cupon.id}>{cupon.codigoCupon}</li>
                  ))}
                </ul>
              ) : (
                <p>No hay cupones disponibles.</p>
              )}
            </div>
          </Col>
        </Row>
      </div>
    </>
  );
};

export default CarritoCompras;
