import React, { useState, useEffect } from "react";
import { obtenerCarrito, agregarAlCarrito } from "../services/apiCarrito";
import { Row, Col, Card, CardBody, CardHeader, Button } from "reactstrap";
import PanelHeader from "components/PanelHeader/PanelHeader.js";

function Carrito({ carritoId }) {
  const [carrito, setCarrito] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCarrito = async () => {
      try {
        const data = await obtenerCarrito(carritoId);
        if (!data.listaDeProductos || data.listaDeProductos.length === 0) {
          setError("Tu carrito está vacío.");
        } else {
          setCarrito(data);
        }
      } catch (error) {
        setError("Error al cargar el carrito.");
        console.error(error);
      }
    };

    fetchCarrito();
  }, [carritoId]);

  const handleAgregarAlCarrito = async (libroId) => {
    if (!libroId) {
      console.error("libroId es undefined");
      return;
    }

    try {
      await agregarAlCarrito(libroId);
      alert("Libro agregado al carrito");
    } catch (error) {
      console.error("Error al agregar al carrito:", error);
    }
  };

  if (error) {
    return (
      <>
        <PanelHeader size="sm" />
        <div className="content">
          <Row>
            <Col xs={12}>
              <Card>
                <CardHeader>
                  <h4 className="title">Carrito de Compras</h4>
                </CardHeader>
                <CardBody>
                  <div>{error}</div>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }

  if (!carrito) {
    return (
      <>
        <PanelHeader size="sm" />
        <div className="content">
          <Row>
            <Col xs={12}>
              <Card>
                <CardHeader>
                  <h4 className="title">Carrito de Compras</h4>
                </CardHeader>
                <CardBody>
                  <div>Cargando...</div>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          {carrito.listaDeProductos.map((producto, index) => (
            <Col md={4} key={producto.libroId}>
              <Card>
                <CardBody>
                  <h5 className="card-title">{producto.tituloLibro}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">{producto.autorLibro || "Autor desconocido"}</h6>
                  <p>Fecha de Publicación: {new Date(producto.fechaPublicacion).toLocaleDateString()}</p>
                  <Button color="primary" onClick={() => handleAgregarAlCarrito(producto.libroId)}>Agregar al carrito</Button>
                </CardBody>
              </Card>
            </Col>
          ))}
        </Row>
      </div>
    </>
  );
}

export default Carrito;
