import React from "react";

function PanelHeader(props) {
  // Estilo en línea para el fondo del panel
  const panelHeaderStyle = {
    position: 'relative',
    padding: '20px',
    backgroundImage: 'url(https://th.bing.com/th/id/R.020baa63669ac025c136d9aa6cd0e77e?rik=baJHL0PtfmeFmQ&pid=ImgRaw&r=0)', // URL de la imagen de fondo
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    borderBottom: '1px solid #ddd',
    color: 'white', // Asegúrate de que el texto sea visible sobre la imagen
    minHeight: '150px', // Ajusta la altura mínima según la imagen
    display: 'flex',
    alignItems: 'center', // Centra el contenido verticalmente
  };

  return (
    <div
      className={
        "panel-header " +
        (props.size !== undefined ? "panel-header-" + props.size : "")
      }
      style={panelHeaderStyle}
    >
      {props.content}
    </div>
  );
}

export default PanelHeader;
