import React, { useState, useEffect, useRef } from "react";
import { Row, Col, Card, CardBody, CardTitle, CardText } from "reactstrap";
import NotificationAlert from "react-notification-alert";
import PanelHeader from "components/PanelHeader/PanelHeader.js";
import { obtenerTodoElCarrito } from '../services/apiCarrito'; // Asegúrate de que la ruta sea correcta

const CarritoCompras = () => {
  const [carritos, setCarritos] = useState([]);
  const notificationAlert = useRef();

  useEffect(() => {
    const fetchCarritos = async () => {
      try {
        const data = await obtenerTodoElCarrito();
        // Filtrar carritos que no tienen productos seleccionados
        const carritosConProductos = data.filter(carrito => carrito.listaDeProductos && carrito.listaDeProductos.length > 0);
        setCarritos(carritosConProductos);
      } catch (error) {
        console.error('Error fetching data:', error);
        notify('danger', 'Error al obtener datos del carrito.');
      }
    };

    fetchCarritos();
  }, []);

  const calcularTotal = (productos) => {
    if (!productos) return 0;
    return productos.reduce((total, producto) => total + (producto.precio || 0), 0);
  };

  const notify = (type, message) => {
    const options = {
      place: 'tr',
      message: <div>{message}</div>,
      type: type,
      icon: "now-ui-icons ui-1_bell-53",
      autoDismiss: 7
    };
    notificationAlert.current.notificationAlert(options);
  };

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <NotificationAlert ref={notificationAlert} />
        <Row>
          {carritos.length > 0 ? (
            carritos.map((carrito) => (
              <Col md={12} key={carrito.carritoId}>
                <Card className="mb-4">
                  <CardBody>
                    <CardTitle tag="h5">Carrito ID: {carrito.carritoId}</CardTitle>
                    <CardText>Fecha de Creación: {new Date(carrito.fechaCreacionSesion).toLocaleString()}</CardText>
                    <CardText tag="h6">Productos:</CardText>
                    {carrito.listaDeProductos.map((producto, index) => (
                      <Row key={producto.libroId + '-' + index} className="mb-2">
                        <Col md={8}>
                          <CardText>Título: {producto.tituloLibro}</CardText>
                          <CardText>Fecha de Publicación: {new Date(producto.fechaPublicacion).toLocaleDateString()}</CardText>
                        </Col>
                        <Col md={4} className="text-right">
                          <CardText>Precio: ${producto.precio ? producto.precio.toFixed(2) : 'Sin precio'}</CardText>
                        </Col>
                      </Row>
                    ))}
                    <CardText tag="h6">Total: ${calcularTotal(carrito.listaDeProductos).toFixed(2)}</CardText>
                  </CardBody>
                </Card>
              </Col>
            ))
          ) : (
            <Col md={12}>
              <Card className="mb-4">
                <CardBody>
                  <CardText>No hay carritos con productos seleccionados.</CardText>
                </CardBody>
              </Card>
            </Col>
          )}
        </Row>
      </div>
    </>
  );
};

export default CarritoCompras;
