import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardBody, FormGroup, Form, Input, Row, Col, Button, Alert } from 'reactstrap';
import PanelHeader from 'components/PanelHeader/PanelHeader';
import { getCuponPorId, actualizarCupon } from '../services/apiCupones';
import { Link, useParams, useNavigate } from 'react-router-dom';

function EditarCupon() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [codigo, setCodigo] = useState('');
  const [porcentajeDescuento, setPorcentajeDescuento] = useState('');
  const [descuentoMinimo, setDescuentoMinimo] = useState('');
  const [alertVisible, setAlertVisible] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertColor, setAlertColor] = useState('success');

  useEffect(() => {
    const fetchCupon = async () => {
      try {
        const cupon = await getCuponPorId(id);
        setCodigo(cupon.cuponCode);
        setPorcentajeDescuento(cupon.porcentajeDescuento);
        setDescuentoMinimo(cupon.descuentoMinimo);
      } catch (error) {
        console.error('Error al obtener cupón:', error);
        setAlertMessage('Error al cargar los datos del cupón');
        setAlertColor('danger');
        setAlertVisible(true);
      }
    };

    fetchCupon();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const cuponActualizado = {
        id,  // Asegúrate de incluir el id aquí
        cuponCode: codigo,
        porcentajeDescuento,
        descuentoMinimo
      };

      const respuesta = await actualizarCupon(cuponActualizado);
      console.log('Cupón actualizado:', respuesta);

      setAlertMessage('Cupón actualizado exitosamente');
      setAlertColor('success');
      setAlertVisible(true);

      setTimeout(() => {
        navigate('/admin/lista-cupones');
      }, 2000);
    } catch (error) {
      console.error('Error al actualizar cupón:', error);

      setAlertMessage('Error al actualizar cupón');
      setAlertColor('danger');
      setAlertVisible(true);
    }
  };

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col md="8">
            <Card>
              <CardHeader>
                <Row>
                  <Col>
                    <h5 className="title">Editar Cupón</h5>
                  </Col>
                </Row>
                <Row>
                  <Col>
                    <Link to="/admin/lista-cupones">
                      <Button color="secondary" className="btn-round btn-sm">
                        Regresar a la lista
                      </Button>
                    </Link>
                  </Col>
                </Row>
              </CardHeader>
              <CardBody>
                {alertVisible && (
                  <Alert color={alertColor} isOpen={alertVisible} toggle={() => setAlertVisible(false)}>
                    <span>
                      <b>{alertColor === 'success' ? 'Éxito - ' : 'Error - '}</b>
                      {alertMessage}
                    </span>
                  </Alert>
                )}
                <Form onSubmit={handleSubmit}>
                  <Row>
                    <Col className="pr-1" md="6">
                      <FormGroup>
                        <label>Código</label>
                        <Input
                          value={codigo}
                          onChange={(e) => setCodigo(e.target.value)}
                          placeholder="Código"
                          type="text"
                          required
                        />
                      </FormGroup>
                    </Col>
                    <Col className="pl-1" md="6">
                      <FormGroup>
                        <label>Porcentaje de Descuento</label>
                        <Input
                          value={porcentajeDescuento}
                          onChange={(e) => setPorcentajeDescuento(e.target.value)}
                          placeholder="Porcentaje de Descuento"
                          type="number"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <label>Descuento Mínimo</label>
                        <Input
                          value={descuentoMinimo}
                          onChange={(e) => setDescuentoMinimo(e.target.value)}
                          placeholder="Descuento Mínimo"
                          type="number"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Button type="submit" color="primary">
                    Actualizar Cupón
                  </Button>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default EditarCupon;
