function TablaCupones() {
    const [cupones, setCupones] = useState(null);
    const [loading, setLoading] = useState(true);
  
    useEffect(() => {
      const fetchData = async () => {
        try {
          const data = await getCupones();
          console.log('Datos recibidos:', data); // Verifica qué datos se están recibiendo
          setCupones(data);
          setLoading(false);
        } catch (error) {
          console.error('Error al obtener cupones:', error);
          setLoading(false);
        }
      };
  
      fetchData();
    }, []);
  
    const formatDate = (dateString) => {
      if (!dateString) return '';
  
      const date = new Date(dateString);
      const year = date.getFullYear();
      const month = (1 + date.getMonth()).toString().padStart(2, '0');
      const day = date.getDate().toString().padStart(2, '0');
  
      return `${day}/${month}/${year}`;
    };
  
    if (loading) {
      return (
        <div className="text-center">
          <Spinner color="primary" />
          <p className="mt-2">Cargando cupones...</p>
        </div>
      );
    }
  
    if (!cupones || cupones.length === 0) {
      return <p>No se encontraron cupones.</p>;
    }
  
    return (
      <>
        <PanelHeader size="sm" />
        <div className="content">
          <Row>
            <Col xs={12}>
              <Card>
                <CardHeader>
                  <h4 className="title">Cupones</h4>
                </CardHeader>
                <CardBody>
                  <Table responsive>
                    <thead className="text-primary">
                      <tr>
                        <th>Código</th>
                        <th>Porcentaje de Descuento (%)</th>
                        <th>Descuento Mínimo</th>
                        <th>Fecha de Creación</th>
                      </tr>
                    </thead>
                    <tbody>
                      {cupones.map((cupon, index) => (
                        <tr key={index}>
                          <td>{cupon.codigo}</td>
                          <td>{cupon.porcentajeDescuento}</td>
                          <td>{cupon.descuentoMinimo}</td>
                          <td>{formatDate(cupon.fechaCreacion)}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
  
  export default TablaCupones;
