import React, { useState, useEffect } from "react";
import { Card, CardHeader, CardBody, FormGroup, Form, Input, Row, Col, Button, Alert } from "reactstrap";
import PanelHeader from "components/PanelHeader/PanelHeader.js";
import { agregarLibro, getAutores } from "../services/apiLibro.js";

function AgregarLibro() {
  const [titulo, setTitulo] = useState("");
  const [autor, setAutor] = useState("");
  const [fechaPublicacion, setFechaPublicacion] = useState("");
  const [precio, setPrecio] = useState("");
  const [imagen, setImagen] = useState(null);
  const [imagenPreview, setImagenPreview] = useState(null);
  const [autores, setAutores] = useState([]);
  const [alertVisible, setAlertVisible] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [alertColor, setAlertColor] = useState("success");

  useEffect(() => {
    const fetchAutores = async () => {
      try {
        const respuesta = await getAutores();
        setAutores(respuesta);
      } catch (error) {
        console.error("Error al obtener autores:", error);
      }
    };

    fetchAutores();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const autorSeleccionado = autores.find((a) => a.guid === autor);
      console.log("Autor seleccionado:", autorSeleccionado);
      const nombreAutor = autorSeleccionado ? autorSeleccionado.nombre : "";

      const nuevoLibro = { titulo, autor: nombreAutor, fechaPublicacion, precio };
      const respuesta = await agregarLibro(nuevoLibro, imagen);

      console.log("Libro agregado:", respuesta);

      setTitulo("");
      setAutor("");
      setFechaPublicacion("");
      setPrecio("");
      setImagen(null);
      setImagenPreview(null);

      setAlertMessage("Libro agregado exitosamente");
      setAlertColor("success");
      setAlertVisible(true);
    } catch (error) {
      console.error("Error al agregar libro:", error);

      setAlertMessage("Error al agregar libro");
      setAlertColor("danger");
      setAlertVisible(true);
    }
  };

  const handleImagenChange = (e) => {
    const selectedImagen = e.target.files[0];
    setImagen(selectedImagen);

    const reader = new FileReader();
    reader.onloadend = () => {
      setImagenPreview(reader.result);
    };
    reader.readAsDataURL(selectedImagen);
  };

  return (
    <>
      <PanelHeader size="sm" />
      <div className="content">
        <Row>
          <Col md="8">
            <Card>
              <CardHeader>
                <h5 className="title">Agregar Libro</h5>
              </CardHeader>
              <CardBody>
                {alertVisible && (
                  <Alert color={alertColor} isOpen={alertVisible} toggle={() => setAlertVisible(false)}>
                    <span>
                      <b>{alertColor === "success" ? "Success - " : "Danger - "}</b>
                      {alertMessage}
                    </span>
                  </Alert>
                )}
                <Form onSubmit={handleSubmit}>
                  <Row>
                    <Col className="pr-1" md="6">
                      <FormGroup>
                        <label>Título</label>
                        <Input
                          value={titulo}
                          onChange={(e) => setTitulo(e.target.value)}
                          placeholder="Título"
                          type="text"
                          required
                        />
                      </FormGroup>
                    </Col>
                    <Col className="pl-1" md="6">
                      <FormGroup>
                        <label>Autor</label>
                        <Input
                          type="select"
                          value={autor}
                          onChange={(e) => {
                            console.log("Autor cambiado a:", e.target.value);
                            setAutor(e.target.value);
                          }}
                          required
                        >
                          <option value="">Selecciona un autor</option>
                          {autores.map((autor) => (
                            <option key={autor.guid} value={autor.guid}>
                              {autor.nombre} {autor.apellido}
                            </option>
                          ))}
                        </Input>
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="6">
                      <FormGroup>
                        <label>Fecha de Publicación</label>
                        <Input
                          value={fechaPublicacion}
                          onChange={(e) => setFechaPublicacion(e.target.value)}
                          placeholder="Fecha de Publicación"
                          type="date"
                          required
                        />
                      </FormGroup>
                    </Col>
                    <Col md="6">
                      <FormGroup>
                        <label>Precio</label>
                        <Input
                          value={precio}
                          onChange={(e) => setPrecio(e.target.value)}
                          placeholder="Precio"
                          type="number"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <label>Imagen</label>
                        <Input
                          type="file"
                          accept="image/*"
                          onChange={handleImagenChange}
                          required
                        />
                      </FormGroup>
                      {imagenPreview && (
                        <img src={imagenPreview} alt="Vista previa de la imagen" style={{ maxWidth: '100%', maxHeight: '300px' }} />
                      )}
                    </Col>
                  </Row>
                  <Button type="submit" color="primary">Agregar Libro</Button>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default AgregarLibro;
